import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch('https://anapioficeandfire.com/api/houses')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render() {
    var { isLoaded, items } =  this.state;

      if(!isLoaded) {
        return <div>Loading..</div>;
      }

    return (
      <div className="App">
        <h1>My Second React Application</h1>
      </div>
    );
  }

} 

export default App;
