import React, { Component } from 'react';

class HouseDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
          items: [],
          isLoaded: false,
        }
      }

      componentDidMount() {
        fetch('https://anapioficeandfire.com/api/houses')
          .then(res => res.json())
          .then(json => {
            this.setState({
              isLoaded: true,
              items: json,
            })
          });
      }

    render() {
    var { isLoaded, items } =  this.state;

    if(!isLoaded) {
      return <div>Loading..</div>;
    }
    else {
      return (
        <div className="house">
          <ul className="house__list">
            {items.map(item => (
              <li key={item.name} className="house__item">
                <a href="#">Name: {item.name}</a>
                <ul className="house__details">
                  <li>Url: {item.url}</li>
                  <li>Region: {item.region}</li>
                  <li>CoatOfArms: {item.coatOfArms}</li>
                  <li>Titles: {item.titles}</li>
                  <li>Words: {item.word}</li>
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )
    }
  }
}

export default HouseDetails;
