import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
			isLoaded: false,
			clickedItem: null,
    }
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch('https://anapioficeandfire.com/api/houses')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
					items: json,
        })
      });
  }

  handleClick = i => {
		this.setState({clickedItem: i});
  }



  render() {
    var { isLoaded, items } =  this.state;
    var someElementClass = this.state.clickedItem === i ? 'clicked' : '';

    if(!isLoaded) {
      return <div>Loading..</div>;
    }
    else {
      return (
        <div className="house">
					<h1 className="house__headline">List of Houses from game of Thrones</h1>
          <ul className="house__list">
            {items.map((item, i) => (
              <li key={item.name} className="house__item">
                <a href="#" className="house__link" onClick={this.handleClick}>Name: {item.name}</a>
                <ul className={"house__details" + (this.state.active ? "house__details--active" :"" )}>
									<h2 className="house__details-headline">House Details</h2>
                  <li className="house__details-item">Url: {item.url}</li>
                  <li className="house__details-item">Region: {item.region}</li>
                  <li className="house__details-item">CoatOfArms: {item.coatOfArms}</li>
                  <li className="house__details-item">Titles: {item.titles}</li>
                  <li className="house__details-item">Words: {item.word}</li>
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )
    }
  }
} 

export default App;
